import './App.css';


const App = () => {
  const users = [
    {name:"Pedro",age: 21},
    {name:"Jack",age: 25},
    {name:"Jessica",age: 45}
  ];

  return <div className='App'>
    {users.map((user) => {
    <User name={user.name} age={user.age} />
  })} </div>

};

const User =(props) =>{
  return (<div>
    <h1 style={{color:"green"}}>{props.name}</h1>;
    <h1 style={{color:"red"}}>{props.age}</h1>
  </div>);
};

export default App
